var room = msg.payload;
var storeName = "store";

var options = flow.get("options", storeName);
var specific = options["specific"]["global"] ? false : options["specific"][room];
var lookup = (field => options[field][specific ? room : "global"]);
var auto = lookup("auto");

return [[
    {"room": room, "field": "specific", "payload": specific},
    {"room": room, "field": "auto", "payload": auto},
    {"room": room, "field": "mode", "payload": auto ? flow.get("autoMode", storeName) : lookup("mode")},
    {"room": room, "field": "position", "payload": lookup("position")},
    {"room": room, "field": "active", "payload": lookup("active")},
    {"room": room, "field": "from_earliest_workday", "payload": lookup("fromEarliestWorkday")},
    {"room": room, "field": "until_latest_workday", "payload": lookup("untilLatestWorkday")},
    {"room": room, "field": "from_earliest_holiday", "payload": lookup("fromEarliestHoliday")},
    {"room": room, "field": "until_latest_holiday", "payload": lookup("untilLatestHoliday")}
]];

var domain = type => `input_${type}`;

function entity (type, field, room) {
    return domain(type) + `.blind_control_${field}_${room}`
}

function service (type, value) {
    return {
        "boolean": value ? "turn_on" : "turn_off",
        "select": "select_option",
        "number": "set_value",
        "datetime": "set_datetime"
    }[type]
}

function data (type, value) {
    switch (type) {
        case "select":
            return {"option": value};
        case "number":
            return {"value": value};
        case "datetime":
            return {"time": value};
    }
}

function message (room, field, value) {
    var type = {
        "specific": "boolean",
        "auto": "boolean",
        "mode": "select",
        "active": "boolean",
        "position": "number",
        "from_earliest_workday": "datetime",
        "until_latest_workday": "datetime",
        "from_earliest_holiday": "datetime",
        "until_latest_holiday": "datetime"
    }[field];
    return {
        "domain": domain(type),
        "service": service(type, value),
        "entity_id": entity(type, field, room),
        "data": data(type, value)
    }
}

return message(msg.room, msg.field, msg.payload);

var header = {
    "room": msg.room,
    "reason": "manual"
};

positionMessages = []
switch (msg.payload) {
    case -20:
        msg.payload = -10;
        positionMessages.push({
            ...header,
            "reset": true
        });
        break
    case -10:
        break;
    default:
        positionMessages.push({
            ...header,
            "position": msg.payload / 100
        });
}

return [positionMessages, msg];

var modes = {
    "aus": 0,
    "Winter": 1,
    "Sonnenlicht minimieren": 3,
    "Sommer": 16
};

return [
    {"room": msg.room, "mode": modes[msg.payload]},
    msg
];

const startup_scene = {brightness: 254, color_temp: 370};
const additional_scenes = [
    {brightness: 254, xy_color: [0.314951744809611, 0.325010746064094]} // activate
    , {brightness: 254, xy_color: [0.314951744809611, 0.325010746064094]} // reading
    , {brightness: 213, xy_color: [0.435684, 0.281409]} // spring flowers
    , {brightness: 77, xy_color: [0.458227749642249, 0.410224893445347]} // dimmed
] || []; // && to exclude, || to include additional scenes

const scenes = [
    startup_scene
    , ...additional_scenes
];

result = {
    service: "turn_on"
    , data: {}
};
if (msg.count > -1) {
    Object.assign(
        result.data
        , scenes[msg.count % scenes.length] // round robin
    );
}

return result;

return {
    service: "turn_off"
    , data: {}
};

var brightness = (step) => {
    return {
        service: "turn_on"
        , data: {brightness_step: step}
    }
};

var stop = {payload: "stop"}

return {
    "up-press": [brightness(40), null],
    "down-press": [brightness(-40), null],
    "up-hold": [null, brightness(40)],
    "down-hold": [null, brightness(-40)],
    "up-hold-release": [null, stop],
    "down-hold-release": [null, stop]
}[msg.payload];

payload = {
    "on-hold": {color_temp_move: 60},
    "off-hold": {color_temp_move: -60},
    "on-hold-release": {color_temp_move: "stop"},
    "off-hold-release": {color_temp_move: "stop"}
}[msg.payload];

return {payload: payload};

function status() {
    var print = (s => (typeof s == "string") ? s : "undefined");
    node.status({"text": `state: ${print(context.get("state"))}, value: ${print(context.get("value"))}, direction: ${print(context.get("direction"))}, key: ${print(context.get("key"))}`});
}

function setContext(key, value) {
    context.set(key, value);
    status();
}

function message (key) {
    setContext("key", key);
    return {"payload": key};
}

if (msg.topic == "state") {
    setContext("state", msg.payload);
    return;
}

var state = context.get("state");
if (!state.match("^(on|off)$")) {
    return;
}

var direction = context.get("direction");
var value = msg.value;
setContext("value", value);
switch (value) {
    case "KeyPressed":
        return message((state == "on") ? "off-press" : "on-press");
    case "KeyPressed2x":
        return message("on-press");
    case "KeyHeldDown":
        return message(`${direction}-hold`);
    case "KeyReleased":
        setContext("direction", direction == "up" ? "down" : "up");
        return message(`${direction}-hold-release`);
}

