var event = msg.payload.event;

var action = {
    short_release: "press"
    , long_release: "hold-release"
    , repeat: "hold"
}[event.type]

if (action) {
    var subtype = {1: "on", 2: "up", 3: "down", 4: "off"}[event.subtype];
    return {
        id: event.id
        , payload: `${subtype}-${action}`
    };
}

var payload = {state: "off"};

return {
    room: msg.room,
    payload: payload
};

