const startup_scene = {brightness: 254, color_temp: 370};
const additional_scenes = [
  {brightness: 254, xy_color: [0.314951744809611, 0.325010746064094]} // activate
  , {brightness: 254, xy_color: [0.314951744809611, 0.325010746064094]} // reading
  , {brightness: 213, xy_color: [0.435684, 0.281409]} // spring flowers
  , {brightness: 77, xy_color: [0.458227749642249, 0.410224893445347]} // dimmed
] || []; // && to exclude, || to include additional scenes

const scenes = [
    startup_scene
    , ...additional_scenes
];

var payload = (msg.count == -1) ? {state: "on"} : scenes[msg.count % scenes.length]; // round robin

return {
    room: msg.room,
    payload: payload
};

counters = context.get("counters");
var room = msg.room;

counters[room] = ("reset" in msg) ? -2 : (room in counters ? counters[room] : -2) + 1;
msg.count = counters[room];

return msg;

if (typeof msg.delay == "number") {
    context.set("delay", msg.delay)
}
const delay = context.get("delay"); 

function setState (state) {
    context.set("state", state);
    var deadline = context.get("deadline");
    var status = {
        "fill": state ? "red" : "green",
        "shape": deadline ? "ring" : "dot"  ,
        "text": state + (deadline ? ` (until ${deadline})` : "")
    };
    node.status(status);
}

function set (timeout, deadline, state) {
    context.set("timeout", timeout);
    context.set("deadline", deadline);
    setState(state);    
}

function send (msg) {
    set(undefined, undefined, msg.payload);
    node.send(msg);
}

var state = context.get("state");
var timeout = context.get("timeout");
var deadline = context.get("deadline");

if (typeof msg.payload !== "boolean") {
    return msg;
} 

if (!state) {
    setState(msg.payload);
    return msg;
}

if (msg.payload) {
    if (timeout) {
        clearTimeout(timeout);
        set(undefined, undefined, state);
    }
    return;
}

if (!timeout) {
    set(
        setTimeout(send, delay, msg),
        new Date(Date.now() + delay).toLocaleTimeString('us-US', {"hourCycle": "h23"}),
        state
    );
}
